            Restaurent ID :{this.props.restaurantID}
        </h2>

        {/* <p className="body-content">{this.props.text}</p> */}

        <p className="body-content">

        <div>
            Restaurent Name: {this.props.restaurantName}
        </div>   
        <div>
            Restaurants Cuisines:{this.props.cuisines}
        </div>
            <div>
            Average Cost For Two: {this.props.averageCostForTwo}
            </div>
            <div>
              Currency: {this.props.currency}
            </div>

        </p>

        <Button />
      </div>
    )
  }
}
